import React from 'react';
import vid from '../../my-project/src/Vid/lv_0_20230402134428_1.mp4';
import logo from '../../my-project/src/Images/Salz Logo.jpg';

const About = () => {
  return (
    <div className='background-about'>
        <div className='overlay'></div>
      <div className='vid1'>
        <video src={vid}  className='vid ' autoPlay loop  muted playsInline  /> 
        </div>
        <div className='content'>
            <img src={logo} alt='' className='image-fluid logo top'/>
            <div className='border1'>
                <h2 className='fontsize mx-2'>Helping individuals,<br/> brands & businesses get more <br/>out of podcasting.</h2>
            <p className='mx-2'>Through our podcast, we expose different podcasters to users as well as sites thet can teach  those interested in starting their own podcast.</p>
            </div>
        </div>
      
    </div>
  );
}

export default About;
